;(function($){$.fn.counter=function(length,text){text=text||" caractère(s) restant(s)"
return this.each(function(){if(!$(this).data('txt_count')){var counter=$('<p class="textarea_counter"><span style="margin-top: 2px;"></span>&nbsp;'+text+'</p>');$(this).data('txt_count',1).after(counter);$(this).keyup(function(){var left=size=$(this).val().length;if(length>0){left=(length-size>0)?length-size:0;$(this).val(($(this).val()).substring(0,length));}
counter.children('span:first').html(left);}).keyup();}});}})(jQuery)